    def agregarCategoria(self):
        id = self.agrProductoId.text()
        nombre = self.agrNombreProducto.text()
        descripcion = self.descripcion_categoria.toPlainText()
        self.controladorProductos.agregarCategoria(id,nombre,descripcion)
        self.nombreCategorias()

    def eliminarCategoria(self):
        elemento_seleccionado = self.producto_a_eliminar_2.currentText()
        self.controladorProductos.eliminarCategoria(elemento_seleccionado)
        self.nombreCategorias()
        
    def nombreCategorias(self):
        self.producto_a_eliminar_2.clear()
        categorias = self.controladorProductos.obtenerCategorias()
        self.producto_a_eliminar_2.addItem("Seleccionar cédula")
        for categoria in categorias:
            self.producto_a_eliminar_2.addItem(categoria[0])
        self.producto_a_eliminar_2.setCurrentIndex(0)   
        
    def modificarCategoria(self):
        id = self.modificar_id_buscar.text()
        self.controladorProductos.modificarCategoria(id)  
        
    def categorias(self):
        self.empleado_eliminar.clear()
        cedulas = self.controlador_empleados.obtenerCedulas()
        self.empleado_eliminar.addItem("Seleccionar cédula")
        for cedula in cedulas:
            self.empleado_eliminar.addItem(cedula[0])
        self.empleado_eliminar.setCurrentIndex(0)
    
